import React from 'react';
import './App.css';

class App extends React.Component {
  constructor(props){
    super(props);

    this.state={
      url: '',
      email: '',
      price: ''
    }

  }


  render(){
    return (
      <div className="background">
          <h1><span className="amazon"></span> Amazon Price Checker</h1>
          <p>This app will send you an email when the price of a product goes down. Click the button below!</p>   
          <form 
          name="myForm" 
          id='myForm'>
                    <p> Enter Your Information Here: </p>
                    <p1 className="labels"> Email: </p1>
                      <input type="email" name='email' id="user" placeholder="Example@gmail.com" required/>
                    <p1 className="labels"> Url:  </p1>
                      <input type="url" name='url' id="url" placeholder="amazon.com" required/>
                    <p1 className="labels">Minimum Price:</p1>
                      <input type="number" id="minPrice" min="1" name='minPrice' placeholder="10.00" required/>
                    <button type="submit" id='submitButton'>Submit</button>
          </form>   
      </div>
    );
  }
}

export default App;
